(function( exports, $ ) {
    var api = sedApp.editor;

    $( function() {

        api.shortcodeUpdate.sed_social_bar= {

            number_items : function( id , value ){
                var patternId = $( '[sed_model_id="' + id + '"]' ).siblings(".add-item-social-bar:first").attr("sed_model_id") ,
                    acds = $( '[sed_model_id="' + id + '"]' ).find("li") ,
                    acdsLen = acds.length;

                 api.addModelPattern({
                    newValue        : value  ,
                    oldValue        : acdsLen  ,
                    type            : "single"  ,
                    id              : id  ,
                    patternId       : patternId  ,
                    list            : acds  ,
                    attrs           : [],
                    shortcode_name  : "sed_social_bar"  ,
                    refresh         : true ,
                    //group           :   ,
                    //max             :   ,
                    min             : 1
                    //onCreate        : function(){} ,
                    //onAddPatten     : function(){} ,
                 });
            }

        };


    });


}(sedApp, jQuery));